SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE far_atualizar_vl_total_ped (nr_seq_pedido_p bigint) AS $body$
DECLARE


vl_total_pedido_w		double precision;
vl_mercadoria_w		double precision;


BEGIN

select	coalesce(sum(vl_liquido_item),0)
into STRICT	vl_mercadoria_w
from	far_pedido_item
where	nr_seq_pedido	= nr_seq_pedido_p
and	coalesce(nr_seq_motivo_exclusao::text, '') = '';

select	((coalesce(vl_mercadoria_w,0) + coalesce(vl_entrega,0)) - coalesce(vl_desconto,0))
into STRICT	vl_total_pedido_w
from	far_pedido
where	nr_sequencia = nr_seq_pedido_p;

update	far_pedido
set	vl_mercadoria	= vl_mercadoria_w,
	vl_total		= vl_total_pedido_w
where	nr_sequencia 	= nr_seq_pedido_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
