CREATE OR REPLACE PROCEDURE far_atualiza_compl_pf ( cd_pessoa_fisica_p text, cd_cep_p text, ds_endereco_p text, nr_endereco_p text, ds_complemento_p text, ds_bairro_p text, ds_municipio_p text, sg_estado_p text, nr_ddi_telefone_p text, nr_ddd_telefone_p text, nr_telefone_p text, ds_fone_adic_p text, ds_observacao_p text) AS $body$
DECLARE


qt_existe_w 	integer;
tag_pais_w	varchar(15);


BEGIN
select	max(ds_locale)
into STRICT	tag_pais_w
from	user_locale 
where	nm_user = obter_usuario_ativo;

select	count(*)
into STRICT	qt_existe_w
from	compl_pessoa_fisica
where	cd_pessoa_fisica = cd_pessoa_fisica_p
and	ie_tipo_complemento = 1; -- Tipo residencial
if (qt_existe_w > 0) then
	if (tag_pais_w in ('de_DE', 'de_AT'))then
		update	compl_pessoa_fisica
		set	cd_cep 			= cd_cep_p,
			ds_endereco 		= ds_endereco_p,
			ds_compl_end 		= nr_endereco_p,
			ds_complemento 		= ds_complemento_p,
			ds_bairro 			= ds_bairro_p,
			ds_municipio 		= ds_municipio_p,
			sg_estado 		= sg_estado_p,
			nr_ddi_telefone 		= nr_ddi_telefone_p,
			nr_ddd_telefone 		= nr_ddd_telefone_p,
			nr_telefone 		= nr_telefone_p,
			ds_fone_adic 		= ds_fone_adic_p,
			dt_atualizacao 		= clock_timestamp(),
			ds_observacao 		= ds_observacao_p
		where	cd_pessoa_fisica		= cd_pessoa_fisica_p
		and	ie_tipo_complemento 	= 1;
	else
		update	compl_pessoa_fisica
		set	cd_cep 			= cd_cep_p,
			ds_endereco 		= ds_endereco_p,
			nr_endereco 		= nr_endereco_p,
			ds_complemento 		= ds_complemento_p,
			ds_bairro 			= ds_bairro_p,
			ds_municipio 		= ds_municipio_p,
			sg_estado 		= sg_estado_p,
			nr_ddi_telefone 		= nr_ddi_telefone_p,
			nr_ddd_telefone 		= nr_ddd_telefone_p,
			nr_telefone 		= nr_telefone_p,
			ds_fone_adic 		= ds_fone_adic_p,
			dt_atualizacao 		= clock_timestamp(),
			ds_observacao 		= ds_observacao_p
		where	cd_pessoa_fisica		= cd_pessoa_fisica_p
		and	ie_tipo_complemento 	= 1;
	end if;
end if;

commit;

end;
$body$
