CREATE OR REPLACE PROCEDURE far_atualiza_reserva (nr_seq_reserva_p bigint, ie_opcao_p text) AS $body$
DECLARE


/*Opções:
L: Liberar a reserva;
B: Baixar a reserva;
*/
BEGIN

if (ie_opcao_p = 'L') then
	begin

	update	far_reserva_mat
	set	dt_liberacao = clock_timestamp()
	where	nr_sequencia = nr_seq_reserva_p;

	end;
elsif (ie_opcao_p = 'B') then
	begin

	update	far_reserva_mat
	set	dt_baixa = clock_timestamp()
	where	nr_sequencia = nr_seq_reserva_p;

	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
