CREATE OR REPLACE PROCEDURE far_atualiza_saida_ret_ent (nr_seq_romaneio_p bigint, ie_opcao_p text) AS $body$
DECLARE


/* ie_opcao_p:
S - Atualizar Sa√≠da
R - Atualizar Retorno
*/
BEGIN

if (nr_seq_romaneio_p IS NOT NULL AND nr_seq_romaneio_p::text <> '') and (ie_opcao_p = 'S') then
	begin

	update	far_rom_entrega
	set	dt_saida	= 	clock_timestamp()
	where	nr_sequencia =	nr_seq_romaneio_p
	and	coalesce(dt_saida::text, '') = '';

	commit;

	end;

elsif (nr_seq_romaneio_p IS NOT NULL AND nr_seq_romaneio_p::text <> '') and (ie_opcao_p = 'R') then
	begin

	update	far_rom_entrega
	set	dt_retorno = 	clock_timestamp()
	where	nr_sequencia =	nr_seq_romaneio_p
	and	coalesce(dt_retorno::text, '') = '';

	update	far_rom_entrega_pedido
	set	dt_entrega_real =	clock_timestamp()
	where	nr_seq_romaneio =	nr_seq_romaneio_p
	and	coalesce(dt_entrega_real::text, '') = ''
	and	coalesce(nr_seq_motivo_entrega::text, '') = '';

	commit;

	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
