CREATE OR REPLACE PROCEDURE far_atualiza_valor_desconto ( vl_desconto_p bigint, nr_seq_pedido_p bigint, nr_seq_item_p bigint, nr_seq_motivo_desc_p bigint, ie_opcao_p text) AS $body$
DECLARE


vl_total_item_w		double precision;
pr_desconto_w		double precision;
vl_liquido_item_w		double precision;


BEGIN

if (vl_desconto_p IS NOT NULL AND vl_desconto_p::text <> '') and (nr_seq_pedido_p IS NOT NULL AND nr_seq_pedido_p::text <> '') then
	begin

	if (ie_opcao_p = 'P') then
		begin

		update	far_pedido
		set	vl_desconto = vl_desconto_p,
			nr_seq_motivo_desc = nr_seq_motivo_desc_p
		where	nr_sequencia = nr_seq_pedido_p;

		end;

	elsif (ie_opcao_p = 'I') and (coalesce(nr_seq_item_p,0) > 0) then
		begin

		select	vl_total_item
		into STRICT	vl_total_item_w
		from	far_pedido_item
		where	nr_seq_pedido = nr_seq_pedido_p
		and	nr_seq_item = nr_seq_item_p;

		pr_desconto_w	:= (vl_desconto_p * 100 / vl_total_item_w);

		vl_liquido_item_w	:=(vl_total_item_w - vl_desconto_p);

		update	far_pedido_item
		set	vl_desconto = vl_desconto_p,
			pr_desconto = pr_desconto_w,
			nr_seq_motivo_desc = nr_seq_motivo_desc_p,
			vl_liquido_item = vl_liquido_item_w
		where	nr_seq_pedido = nr_seq_pedido_p
		and	nr_seq_item = nr_seq_item_p;

		end;
	end if;

	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
