CREATE OR REPLACE PROCEDURE far_cancela_pedido_pend ( cd_estabelecimento_p bigint) AS $body$
BEGIN

update	far_pedido
set	dt_cancelamento	= clock_timestamp(),
	ie_motivo_baixa	= 'B'
where	cd_estabelecimento = cd_estabelecimento_p
and	ie_classificacao = 'P'
and	coalesce(dt_fechamento::text, '') = ''
and	trunc(dt_pedido) < trunc(clock_timestamp())
and	coalesce(dt_cancelamento::text, '') = '';

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
