CREATE OR REPLACE PROCEDURE far_carrega_benef_contrato ( nr_seq_contrato_far_p bigint, nr_seq_contrato_pls_p bigint, nm_usuario_p text) AS $body$
DECLARE


vl_limite_conv_w		double precision;
cd_pessoa_fisica_w	varchar(10);
nr_sequencia_w		bigint;

c01 CURSOR FOR
SELECT	cd_pessoa_fisica
from	pls_segurado a
where	nr_seq_contrato = nr_seq_contrato_pls_p
and	coalesce(dt_cancelamento::text, '') = ''
and	not exists (select	1
			from	far_contrato_partic x
			where	x.nr_seq_contrato_conv = nr_seq_contrato_far_p
			and	x.cd_pessoa_fisica = a.cd_pessoa_fisica);


BEGIN

if (coalesce(nr_seq_contrato_far_p,0) > 0) and (coalesce(nr_seq_contrato_pls_p,0) > 0) then
	begin

	select	coalesce(max(vl_limite_convenio),0)
	into STRICT	vl_limite_conv_w
	from	far_contrato_conv
	where	nr_sequencia = nr_seq_contrato_far_p;

	open c01;
	loop
	fetch c01 into
		cd_pessoa_fisica_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin

		select	nextval('far_contrato_partic_seq')
		into STRICT	nr_sequencia_w
		;

		insert into far_contrato_partic(
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_seq_contrato_conv,
			cd_pessoa_fisica,
			vl_limite_convenio) values (
				nr_sequencia_w,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				nr_seq_contrato_far_p,
				cd_pessoa_fisica_w,
				vl_limite_conv_w);

		end;
	end loop;
	close c01;

	commit;

	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
