CREATE OR REPLACE PROCEDURE far_estornar_movto_estoque ( nr_seq_venda_p bigint, nm_usuario_p text, nr_seq_nota_fiscal_p bigint default null) AS $body$
DECLARE


nr_movimento_estoque_w			movimento_estoque.nr_movimento_estoque%type;
nr_movimento_estoque_ww			movimento_estoque.nr_movimento_estoque%type;

c01 CURSOR FOR
SELECT	b.nr_movimento_estoque
from	nota_fiscal a,
	movimento_estoque b
where	a.nr_sequencia = b.nr_seq_tab_orig
and	b.ie_origem_documento = '1'
and	b.cd_acao = '1'
and	a.nr_seq_far_venda = nr_seq_venda_p
and	a.nr_sequencia = coalesce(nr_seq_nota_fiscal_p, a.nr_sequencia);


BEGIN

open c01;
loop
fetch c01 into
	nr_movimento_estoque_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	select	nextval('movimento_estoque_seq')
	into STRICT	nr_movimento_estoque_ww
	;

	insert	into movimento_estoque(
		nr_movimento_estoque,
		cd_estabelecimento,
		cd_local_estoque,
		dt_movimento_estoque,
		cd_operacao_estoque,
		cd_acao,
		cd_material,
		dt_mesano_referencia,
		qt_movimento,
		dt_atualizacao,
		nm_usuario,
		ie_origem_documento,
		nr_documento,
		nr_sequencia_item_docto,
		cd_cgc_emitente,
		cd_serie_nf,
		nr_sequencia_documento,
		vl_movimento,
		cd_unidade_medida_estoque,
		cd_procedimento,
		cd_setor_atendimento,
		cd_conta,
		dt_contabil,
		cd_lote_fabricacao,
		dt_validade,
		qt_estoque,
		cd_local_estoque_destino,
		cd_centro_custo,
		cd_unidade_med_mov,
		nr_movimento_estoque_corresp,
		cd_conta_contabil,
		ie_origem_proced,
		cd_fornecedor,
		nr_lote_contabil,
		qt_inventario,
		ds_observacao,
		nr_seq_tab_orig,
		nr_seq_lote_fornec,
		cd_funcao,
		cd_perfil,
		nr_prescricao,
		nr_atendimento)
	SELECT	nr_movimento_estoque_ww,
		cd_estabelecimento,
		cd_local_estoque,
		dt_movimento_estoque,
		cd_operacao_estoque,
		'2',
		cd_material,
		dt_mesano_referencia,
		qt_movimento,
		clock_timestamp(),
		nm_usuario_p,
		ie_origem_documento,
		nr_documento,
		nr_sequencia_item_docto,
		cd_cgc_emitente,
		cd_serie_nf,
		nr_sequencia_documento,
		vl_movimento,
		cd_unidade_medida_estoque,
		cd_procedimento,
		cd_setor_atendimento,
		cd_conta,
		dt_contabil,
		cd_lote_fabricacao,
		dt_validade,
		qt_estoque,
		cd_local_estoque_destino,
		cd_centro_custo,
		cd_unidade_med_mov,
		nr_movimento_estoque_w,
		cd_conta_contabil,
		ie_origem_proced,
		cd_fornecedor,
		nr_lote_contabil,
		qt_inventario,
		ds_observacao,
		nr_seq_tab_orig,
		nr_seq_lote_fornec,
		cd_funcao,
		cd_perfil,
		nr_prescricao,
		nr_atendimento
	from	movimento_estoque
	where	nr_movimento_estoque = nr_movimento_estoque_w;

	insert	into movimento_estoque_valor
	SELECT	nr_movimento_estoque_ww,
		cd_tipo_valor,
		vl_movimento,
		clock_timestamp(),
		nm_usuario_p
	from	movimento_estoque_valor
	where	nr_movimento_estoque = nr_movimento_estoque_w;

end loop;
close c01;

end;
$body$
