SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE far_gerar_acao_log_acesso_site ( nr_seq_log_acesso_p bigint, ie_acao_p text, nr_seq_produto_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_acao_w		bigint;
ds_informacao_w			varchar(255);


BEGIN

if (ie_acao_p in ('ADI','EXI')) and -- Quando  ADICIONA ou EXCLUI o item do carrinho
	(nr_seq_produto_p > 0) then
	begin

	select  wheb_mensagem_pck.get_Texto(313979) || ' ' || cd_material || chr(13) || chr(10) ||
		wheb_mensagem_pck.get_Texto(313980) || ds_descricao
	into STRICT 	ds_informacao_w
	from    	far_produtos_web
	where   	nr_sequencia = nr_seq_produto_p;
	end;
end if;

if (nr_seq_log_acesso_p > 0) then
	select	nextval('far_log_acesso_site_acao_seq')
	into STRICT	nr_sequencia_acao_w
	;

	insert into far_log_acesso_site_acao(		nr_sequencia,
						nr_seq_log_acesso,
						ie_acao,
						ds_informacao,
						dt_atualizacao,
						nm_usuario,
						dt_atualizacao_nrec,
						nm_usuario_nrec)
				values (	nr_sequencia_acao_w,
						nr_seq_log_acesso_p,
						ie_acao_p,
						ds_informacao_w,
						clock_timestamp(),
						nm_usuario_p,
						clock_timestamp(),
						nm_usuario_p);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
