SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE far_gerar_fechamento_convenio ( nr_seq_contrato_conv_p bigint, dt_mesano_referencia_p timestamp, nm_usuario_p text) AS $body$
DECLARE


qt_registro_w		bigint;
nr_sequencia_w		bigint;
nr_seq_pedido_w		bigint;
nr_seq_pedido_fat_w	bigint;
vl_faturamento_w		double precision;
dt_dia_referencia_w	smallint;
dt_mes_ref_inicial_w	timestamp;
dt_mes_ref_final_w	timestamp;
dt_dia_ref_inicial_w	smallint;
dt_dia_ref_final_w		smallint;

c01 CURSOR FOR
SELECT	nr_sequencia
from	far_pedido
where	nr_seq_contrato_conv = nr_seq_contrato_conv_p
and	dt_fechamento between dt_mes_ref_inicial_w and dt_mes_ref_final_w
and	ie_classificacao = 'P';


BEGIN

select	coalesce(max(dt_dia_fechamento),1)
into STRICT	dt_dia_referencia_w
from	far_contrato_conv
where	nr_sequencia = nr_seq_contrato_conv_p;

if (dt_dia_referencia_w < 1) then
	dt_dia_referencia_w	:= 1;
end if;

dt_dia_ref_inicial_w	:= dt_dia_referencia_w;
dt_dia_ref_final_w		:= dt_dia_referencia_w;
dt_mes_ref_inicial_w	:= dt_mesano_referencia_p;
dt_mes_ref_final_w	:= pkg_date_utils.add_month(dt_mesano_referencia_p,1,0);

if (dt_dia_referencia_w > pkg_date_utils.extract_field('DAY', pkg_date_utils.end_of(dt_mes_ref_inicial_w,'MONTH', 0))) then
	dt_dia_ref_inicial_w	:= pkg_date_utils.extract_field('DAY', pkg_date_utils.end_of(dt_mes_ref_inicial_w,'MONTH',0));
end if;

if (dt_dia_referencia_w >  pkg_date_utils.extract_field('DAY', pkg_date_utils.end_of(dt_mes_ref_final_w,'MONTH',0))) then
	dt_dia_ref_final_w	:= pkg_date_utils.extract_field('DAY', pkg_date_utils.end_of(dt_mes_ref_final_w,'MONTH',0));
end if;

dt_mes_ref_inicial_w	:= pkg_date_utils.get_date(dt_dia_ref_inicial_w, dt_mes_ref_inicial_w);
dt_mes_ref_final_w	:= (pkg_date_utils.get_time(pkg_date_utils.get_date(dt_dia_ref_final_w,dt_mes_ref_final_w),23,59,59) -1);

select	count(*)
into STRICT	qt_registro_w
from	far_pedido
where	nr_seq_contrato_conv = nr_seq_contrato_conv_p
and	dt_fechamento between dt_mes_ref_inicial_w and dt_mes_ref_final_w
and	ie_classificacao = 'P';

if (qt_registro_w > 0) then
	begin

	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_sequencia_w
	from	far_contrato_fatur
	where	nr_seq_contrato = nr_seq_contrato_conv_p
	and	trunc(dt_mesano_referencia) = dt_mesano_referencia_p
	and	coalesce(nr_seq_nota::text, '') = '';

	if (nr_sequencia_w <> 0) then
		begin

		delete
		from	far_pedido_fatur
		where	nr_seq_fatur = nr_sequencia_w;
		commit;

		end;

	elsif (nr_sequencia_w = 0) then
		begin

		select	nextval('far_contrato_fatur_seq')
		into STRICT	nr_sequencia_w
		;

		insert into far_contrato_fatur(
			nr_sequencia,
			nm_usuario,
			dt_atualizacao,
			nm_usuario_nrec,
			dt_atualizacao_nrec,
			nr_seq_contrato,
			dt_mesano_referencia) values (
				nr_sequencia_w,
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nr_seq_contrato_conv_p,
				dt_mesano_referencia_p);
		commit;

		end;
	end if;


	open c01;
	loop
	fetch c01 into
		nr_seq_pedido_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin

		select	nextval('far_pedido_fatur_seq')
		into STRICT	nr_seq_pedido_fat_w
		;

		insert into far_pedido_fatur(
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_seq_pedido,
			nr_seq_fatur) values (
				nr_seq_pedido_fat_w,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				nr_seq_pedido_w,
				nr_sequencia_w);

		end;
	end loop;
	close c01;

	commit;

	select	coalesce(sum(vl_total),0)
	into STRICT	vl_faturamento_w
	from	far_pedido a
	where	exists (SELECT	1
			from	far_pedido_fatur x
			where	x.nr_seq_fatur = nr_sequencia_w
			and	x.nr_seq_pedido = a.nr_sequencia);

	update	far_contrato_fatur
	set	vl_faturamento = vl_faturamento_w,
		dt_emissao = clock_timestamp()
	where	nr_sequencia = nr_sequencia_w;

	commit;

	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
