					 cd_pessoa_fisica_p,
					 clock_timestamp(),
					 nm_usuario_p,
					 clock_timestamp(),
					 nm_usuario_p);

	nr_sequencia_log_p := nr_sequencia_log_w;
	end;
end if;

commit;

end;
