SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE far_gerar_novo_pedido ( cd_pessoa_fisica_p text, cd_pessoa_fisica_orig_p text, nr_seq_vendedor_p bigint, nr_cpf_p text, nr_seq_contrato_p bigint, nr_crm_p text, ie_entregar_p text, ie_tipo_complemento_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, nr_seq_pedido_p INOUT bigint) AS $body$
DECLARE


nr_sequencia_w		bigint;

nr_seq_entrega_w		bigint;
ds_endereco_w		varchar(100);
cd_cep_w		varchar(15);
nr_endereco_w		integer;
ds_complemento_w		varchar(40);
ds_bairro_w		varchar(80);
ds_municipio_w		varchar(40);
sg_estado_w		compl_pessoa_fisica.sg_estado%type;

nr_seq_forma_pagto_w	bigint;
nr_seq_pagamento_w	bigint;

vl_entrega_w		double precision;

c01 CURSOR FOR
SELECT	nr_sequencia
from	far_forma_pagamento
where	ie_situacao = 'A';


BEGIN

select	nextval('far_pedido_seq')
into STRICT	nr_sequencia_w
;

insert into far_pedido(
	nr_sequencia,
	cd_estabelecimento,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	dt_pedido,
	cd_pessoa_fisica,
	nr_seq_vendedor,
	ie_entregar,
	nr_seq_contrato_conv,
	ie_cpf_nota,
	ie_troco_entrega,
	nr_crm,
	ie_classificacao,
	nr_cpf,
	vl_total,
	vl_desconto,
	vl_mercadoria,
	ie_status_pedido,
	dt_inicio_atendimento) values (
		nr_sequencia_w,
		cd_estabelecimento_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		cd_pessoa_fisica_p,
		nr_seq_vendedor_p,
		ie_entregar_p,
		nr_seq_contrato_p,
		CASE WHEN nr_cpf_p='' THEN 'N'  ELSE 'S' END ,
		'N',
		nr_crm_p,
		'P',
		nr_cpf_p,
		0,0,0,
		'A',
		clock_timestamp());

if (coalesce(ie_tipo_complemento_p,0) > 0) then
	begin

	select	ds_endereco,
		cd_cep,
		nr_endereco,
		ds_complemento,
		ds_bairro,
		ds_municipio,
		sg_estado
	into STRICT	ds_endereco_w,
		cd_cep_w,
		nr_endereco_w,
		ds_complemento_w,
		ds_bairro_w,
		ds_municipio_w,
		sg_estado_w
	from	compl_pessoa_fisica
	where	cd_pessoa_fisica = cd_pessoa_fisica_orig_p
	and	ie_tipo_complemento = ie_tipo_complemento_p;

	select	nextval('far_pedido_endereco_seq')
	into STRICT	nr_seq_entrega_w
	;

	insert into far_pedido_endereco(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_pedido,
		ie_tipo_endereco,
		ds_endereco,
		cd_cep,
		nr_endereco,
		ds_complemento,
		ds_bairro,
		ds_municipio,
		sg_estado) values (
			nr_seq_entrega_w,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			nr_sequencia_w,
			ie_tipo_complemento_p,
			ds_endereco_w,
			cd_cep_w,
			nr_endereco_w,
			ds_complemento_w,
			substr(ds_bairro_w,1,40),
			ds_municipio_w,
			sg_estado_w);

	end;
end if;

open c01;
loop
fetch c01 into
	nr_seq_forma_pagto_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	select	nextval('far_pedido_forma_pagto_seq')
	into STRICT	nr_seq_pagamento_w
	;

	insert into far_pedido_forma_pagto(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_pedido,
		nr_seq_forma_pagto,
		vl_pagamento) values (
			nr_seq_pagamento_w,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			nr_sequencia_w,
			nr_seq_forma_pagto_w,
			0);

	end;
end loop;
close c01;

select	far_obter_tx_entrega(nr_sequencia_w)
into STRICT	vl_entrega_w
;

update	far_pedido
set	vl_entrega = vl_entrega_w
where	nr_sequencia = nr_sequencia_w;

commit;

CALL FAR_ATUALIZAR_VL_TOTAL_PED(nr_sequencia_w);

nr_seq_pedido_p	:= nr_sequencia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE far_gerar_novo_pedido ( cd_pessoa_fisica_p text, cd_pessoa_fisica_orig_p text, nr_seq_vendedor_p bigint, nr_cpf_p text, nr_seq_contrato_p bigint, nr_crm_p text, ie_entregar_p text, ie_tipo_complemento_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, nr_seq_pedido_p INOUT bigint) FROM PUBLIC;

