CREATE OR REPLACE PROCEDURE far_gerar_pedido_orcamento ( nr_seq_pedido_p bigint, nm_usuario_p text, nr_ped_gerado_p INOUT bigint) AS $body$
DECLARE


nr_sequencia_w			bigint;
cd_estabelecimento_w		smallint;
cd_pessoa_fisica_w		varchar(10);
vl_total_w			double precision;
nr_seq_vendedor_w		bigint;
ie_entregar_w			varchar(1);
nr_seq_contrato_conv_w		bigint;
ie_cpf_nota_w			varchar(1);
ie_troco_entrega_w		varchar(1);
nr_crm_w			varchar(20);
nr_seq_item_w			bigint;
qt_material_w			double precision;
cd_unidade_medida_w		varchar(30);
vl_unitario_item_w			double precision;
vl_total_item_w			double precision;
cd_material_w			integer;

c01 CURSOR FOR
SELECT	nr_seq_item,
	qt_material,
	cd_unidade_medida,
	vl_unitario_item,
	vl_total_item,
	cd_material
from	far_pedido_item
where	nr_seq_pedido = nr_seq_pedido_p
order by	nr_seq_item;


BEGIN

select	cd_estabelecimento,
	cd_pessoa_fisica,
	vl_total,
	nr_seq_vendedor,
	ie_entregar,
	nr_seq_contrato_conv,
	ie_cpf_nota,
	ie_troco_entrega,
	nr_crm
into STRICT	cd_estabelecimento_w,
	cd_pessoa_fisica_w,
	vl_total_w,
	nr_seq_vendedor_w,
	ie_entregar_w,
	nr_seq_contrato_conv_w,
	ie_cpf_nota_w,
	ie_troco_entrega_w,
	nr_crm_w
from	far_pedido
where	nr_sequencia = nr_seq_pedido_p;

select	nextval('far_pedido_seq')
into STRICT	nr_sequencia_w
;

insert into far_pedido(
	nr_sequencia,
	cd_estabelecimento,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	dt_pedido,
	cd_pessoa_fisica,
	vl_total,
	vl_mercadoria,
	nr_seq_vendedor,
	ie_entregar,
	nr_seq_contrato_conv,
	ie_cpf_nota,
	ie_troco_entrega,
	nr_crm,
	nr_seq_orcamento,
	ie_classificacao) values (
		nr_sequencia_w,
		cd_estabelecimento_w,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		cd_pessoa_fisica_w,
		vl_total_w,
		vl_total_w,
		nr_seq_vendedor_w,
		ie_entregar_w,
		nr_seq_contrato_conv_w,
		ie_cpf_nota_w,
		ie_troco_entrega_w,
		nr_crm_w,
		nr_seq_pedido_p,
		'P');

open c01;
loop
fetch c01 into
	nr_seq_item_w,
	qt_material_w,
	cd_unidade_medida_w,
	vl_unitario_item_w,
	vl_total_item_w,
	cd_material_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	insert into far_pedido_item(
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_pedido,
		nr_seq_item,
		qt_material,
		cd_unidade_medida,
		vl_unitario_item,
		vl_total_item,
		vl_liquido_item,
		cd_material) values (
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			nr_sequencia_w,
			nr_seq_item_w,
			qt_material_w,
			cd_unidade_medida_w,
			vl_unitario_item_w,
			vl_total_item_w,
			vl_total_item_w,
			cd_material_w);

	end;
end loop;
close c01;

update	far_pedido
set	ie_motivo_baixa = 'P',
	nm_usuario = nm_usuario_p,
	dt_atualizacao = clock_timestamp()
where	nr_sequencia = nr_seq_pedido_p;

commit;

nr_ped_gerado_p	:= nr_sequencia_w;

end;
$body$
