SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE far_gerar_ped_item_web ( nr_seq_pedido_p bigint, cd_material_p bigint, qt_material_p bigint) AS $body$
DECLARE


cd_estabelecimento_w		integer;
vl_total_item_w 			double precision;
vl_unitario_item_w  		double precision;
nr_sequencia_w			bigint;
vl_total_pedido_w			double precision;
vl_mercadoria_w			double precision;
cd_unidade_venda_w 		varchar(30);


BEGIN

select	cd_estabelecimento
into STRICT	cd_estabelecimento_w
from	far_pedido
where	nr_sequencia = nr_seq_pedido_p;

select 	cd_unidade_venda
into STRICT	cd_unidade_venda_w
from 	material_estab
where	cd_material = cd_material_p
and	cd_estabelecimento = cd_estabelecimento_w;

select	vl_preco
into STRICT	vl_unitario_item_w
from	far_produtos_web
where	cd_material = cd_material_p
and	trunc(clock_timestamp()) between trunc(coalesce(dt_inicio_vigencia,clock_timestamp() - interval '1 days')) and trunc(coalesce(dt_fim_vigencia,clock_timestamp() + interval '1 days'));

vl_total_item_w	:= round((vl_unitario_item_w * qt_material_p)::numeric,2);

select 	coalesce(max(nr_seq_item),0) + 1
into STRICT	nr_sequencia_w
from 	far_pedido_item
where	nr_seq_pedido = nr_seq_pedido_p;

if (coalesce(nr_sequencia_w::text, '') = '') then
	nr_sequencia_w := 1;
end if;

insert into far_pedido_item(
		nr_seq_item,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_pedido,
		qt_material,
		cd_material,
		vl_unitario_item,
		vl_total_item,
		vl_liquido_item,
		cd_unidade_medida,
		vl_desconto) values (
			nr_sequencia_w,
			clock_timestamp(),
			'INTERNET',
			clock_timestamp(),
			'INTERNET',
			nr_seq_pedido_p,
			qt_material_p,
			cd_material_p,
			vl_unitario_item_w,
			vl_total_item_w,
			vl_total_item_w,
			cd_unidade_venda_w,
			0);

select 	coalesce(sum(vl_total_item),0),
	coalesce(sum(vl_liquido_item),0)
into STRICT	vl_mercadoria_w,
	vl_total_pedido_w
from 	far_pedido_item
where	nr_seq_pedido = nr_seq_pedido_p;

update 	far_pedido
set	vl_mercadoria = vl_mercadoria_w,
	vl_total = vl_total_pedido_w
where	nr_sequencia = nr_seq_pedido_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
