CREATE OR REPLACE PROCEDURE far_gerar_reg_falta_mat ( cd_estabelecimento_p bigint, cd_material_p bigint, ie_motivo_falta_p text, cd_pessoa_fisica_p text, nm_usuario_p text, ds_historico_p text) AS $body$
DECLARE

 
nr_seq_reg_falta_w	bigint;
nr_seq_hist_falta_w	bigint;
