		dt_atualizacao_nrec, 
		dt_atualizacao, 
		dt_registro, 
		dt_liberacao, 
		cd_estabelecimento, 
		cd_material, 
		cd_pessoa_fisica, 
		cd_pessoa_resp, 
		ie_motivo_falta) 
	values (	nr_seq_reg_falta_w, 
		nm_usuario_p, 
		nm_usuario_p, 
		clock_timestamp(), 
		clock_timestamp(), 
		clock_timestamp(), 
		clock_timestamp(), 
		cd_estabelecimento_p, 
		cd_material_p, 
		cd_pessoa_fisica_p, 
		cd_pessoa_resp_w, 
		ie_motivo_falta_p);
 
if (ds_historico_p IS NOT NULL AND ds_historico_p::text <> '') then 
	begin 
	 
	select	nextval('far_reg_falta_hist_seq') 
	into STRICT	nr_seq_hist_falta_w 
	;
	 
	insert into	far_reg_falta_hist( 
			nr_sequencia, 
			nm_usuario_nrec, 
			nm_usuario, 
			dt_atualizacao_nrec, 
			dt_atualizacao, 
			nr_seq_reg_falta, 
			ds_historico) 
		values (	nr_seq_hist_falta_w, 
			nm_usuario_p, 
			nm_usuario_p, 
			clock_timestamp(), 
			clock_timestamp(), 
			nr_seq_reg_falta_w, 
			ds_historico_p);
	end;
end if;
	 
commit;
end;
