CREATE OR REPLACE PROCEDURE far_inserir_item_pedido ( nm_usuario_p text, nr_seq_vendedor_p bigint, nr_seq_pedido_p bigint, cd_material_p bigint, qt_material_p bigint, vl_unitario_p bigint, vl_desconto_p bigint, vl_acrescimo_p bigint, cd_estabelecimento_p bigint, cd_pessoa_fisica_p text, nr_seq_motivo_desc_p bigint) AS $body$
DECLARE


qt_pedido_w bigint;
nr_sequencia_w	bigint;

BEGIN

select count(*)
into STRICT	qt_pedido_w
from	far_pedido
where	 nr_sequencia = nr_seq_pedido_p;

if (qt_pedido_w > 0) then

	select 	nextval('far_pedido_item_seq')
	into STRICT	nr_sequencia_w
	from 	far_pedido_item
	where	nr_seq_pedido = nr_seq_pedido_p;

	insert into far_pedido_item(
				nr_seq_item,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				nr_seq_pedido,
				qt_material,
				cd_material,
				vl_unitario_item,
				vl_total_item,
				vl_liquido_item,
				cd_unidade_medida,
				nr_seq_promocao,
				vl_desconto,
				pr_desconto)
			values (nr_sequencia_w,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				nr_seq_pedido_p,
				qt_material_p,
				cd_material_p,
				vl_unitario_p,
				(vl_unitario_p*qt_material_p),
				((vl_unitario_p*qt_material_p) - (vl_desconto_p*qt_material_p) + (vl_acrescimo_p*qt_material_p)),
				0,
				null,
				vl_desconto_p,
				0);

	update	far_pedido
	set		vl_mercadoria = ((vl_unitario_p*qt_material_p) - (vl_desconto_p*qt_material_p) + (vl_acrescimo_p*qt_material_p))
	where	nr_sequencia = nr_seq_pedido_p;

end if;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
