CREATE OR REPLACE PROCEDURE far_libera_comunicado ( nr_seq_comunicado_p bigint, ie_opcao_p text) AS $body$
BEGIN

if (ie_opcao_p = 'L') then
	begin

	update	far_comunicado
	set	dt_liberacao = clock_timestamp()
	where	nr_sequencia = nr_seq_comunicado_p;

	end;
elsif (ie_opcao_p = 'E') then
	begin

	update	far_comunicado
	set	dt_liberacao  = NULL
	where	nr_sequencia = nr_seq_comunicado_p;

	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
