CREATE OR REPLACE PROCEDURE far_medic_controlados ( nr_Seq_pedido_P bigint, nr_seq_item_pedido_p bigint, cd_medico_p text, nr_notificacao_p text, dt_prescricao_p timestamp, nr_barras_p text, nm_usuario_p text) AS $body$
DECLARE

 
contador_w bigint;
cd_estabelecimento_w smallint;

--------------------------------------------------------------------------- 
cd_material_w			bigint;
qt_material_w			double precision;
nr_seq_lote_w			bigint;
nr_seq_LoteAgrup_w		bigint;
cd_kit_mat_w			integer	:= 0;
ds_validade_w			varchar(20);
ds_material_w			varchar(255);
cd_unid_med_w			varchar(30);
nr_sequencia_lp_w			varchar(30);
ds_erro_w			varchar(255);
------------------------------------------------------------------------------ 
 

BEGIN 
cd_estabelecimento_w := wheb_usuario_pck.get_cd_estabelecimento;
 
select	count(*) 
into STRICT	contador_W 
from	far_pedido_item 
where	nr_seq_pedido = nr_seq_pedido_p 
and	nr_Seq_item = nr_Seq_item_pedido_p;
 
if (contador_w > 0) then 
			 
SELECT * FROM Converte_Codigo_Barras( 
		nr_barras_p, cd_estabelecimento_w, null, 'N', cd_material_w, qt_material_w, nr_seq_lote_w, nr_seq_LoteAgrup_w, cd_kit_mat_w, ds_validade_w, ds_material_w, cd_unid_med_w, nr_sequencia_lp_w, ds_erro_w) INTO STRICT cd_material_w, qt_material_w, nr_seq_lote_w, nr_seq_LoteAgrup_w, cd_kit_mat_w, ds_validade_w, ds_material_w, cd_unid_med_w, nr_sequencia_lp_w, ds_erro_w;
 
	 
if (coalesce(nr_seq_lote_w,0) = 0) then 
	CALL WHEB_MENSAGEM_PCK.Exibir_Mensagem_Abort(280321);
end if;
		 
update	far_pedido_item 
set	cd_medico = cd_medico_p, 
	nr_notificacao = nr_notificacao_p, 
	dt_prescricao = dt_prescricao_p, 
	nr_seq_lote_fornec = nr_seq_lote_w, 
	nm_usuario = nm_usuario_p, 
	dt_atualizacao = clock_timestamp() 
where	nr_seq_pedido = nr_seq_pedido_P 
and	nr_seq_item = nr_seq_item_pedido_P;
 
end if;
 
commit;
 
end;
