CREATE OR REPLACE PROCEDURE far_recalcula_preco_pedido ( nr_seq_pedido_p bigint, nr_seq_convenio_p bigint, cd_estabelecimento_p bigint) AS $body$
DECLARE


cd_material_w		integer;
qt_material_w		double precision;
nr_seq_item_w		bigint;
qt_item_w		bigint;
vl_preco_w		double precision;
vl_total_w		double precision;
vl_liquido_w		double precision;
nr_seq_promocao_w	bigint;
vl_desconto_w		double precision;

c01 CURSOR FOR
SELECT	cd_material,
	qt_material,
	nr_seq_item,
	coalesce(vl_desconto,0)
from	far_pedido_item
where	nr_seq_pedido = nr_seq_pedido_p;


BEGIN
-- PROCEDURE REMOVIDA
select	count(*)
into STRICT	qt_item_w
from	far_pedido_item
where	nr_seq_pedido = nr_seq_pedido_p;

if (qt_item_w > 0) then
	begin

	open c01;
	loop
	fetch c01 into
		cd_material_w,
		qt_material_w,
		nr_seq_item_w,
		vl_desconto_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin

		--far_obter_preco_mat(cd_material_w,cd_estabelecimento_p,nr_seq_pedido_p,vl_preco_w,nr_seq_promocao_w);
		--vl_preco_w:= far_obter_preco_mat2(cd_material_w,cd_estabelecimento_p);
		vl_total_w	:= (vl_preco_w * qt_material_w);
		vl_liquido_w	:= vl_total_w;

		if (coalesce(nr_seq_promocao_w,0) = 0) and (coalesce(vl_desconto_w,0) > 0) then
			begin
			vl_liquido_w	:= (vl_total_w - vl_desconto_w);
			end;
		end if;

		update	far_pedido_item
		set	vl_unitario_item 	= vl_preco_w,
			vl_total_item	= vl_total_w,
			vl_liquido_item	= vl_liquido_w,
			nr_seq_promocao	= CASE WHEN nr_seq_promocao_w=0 THEN null  ELSE nr_seq_promocao_w END
		where	nr_seq_pedido	= nr_seq_pedido_p
		and	nr_seq_item	= nr_seq_item_w
		and	cd_material	= cd_material_w;

		if (coalesce(nr_seq_promocao_w,0) > 0) then
			begin

			update	far_pedido_item
			set	vl_desconto = 0,
				pr_desconto = 0
			where	nr_seq_pedido	= nr_seq_pedido_p
			and	nr_seq_item	= nr_seq_item_w
			and	cd_material	= cd_material_w;

			end;
		end if;

		commit;

		end;

	end loop;
	close c01;

	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
