SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE fa_ajustar_medic_continuo ( nr_seq_receita_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_dias_receita_w	integer;
dt_inicio_receita_w	timestamp;
dt_validade_receita_w	timestamp;
ie_atualizar_w		varchar(1);


BEGIN
if (nr_seq_receita_p IS NOT NULL AND nr_seq_receita_p::text <> '') then

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_atualizar_w
	from	fa_receita_farmacia a,
		fa_receita_farmacia_item b
	where	a.nr_sequencia 		= b.nr_seq_receita
	and	a.nr_sequencia		= nr_seq_receita_p
	and	b.ie_uso_continuo 	= 'S'
	and (a.nr_dias_receita	<> b.nr_dias_receita
	or	a.dt_inicio_receita	<> b.dt_inicio_receita
	or	a.dt_validade_receita	<> b.dt_validade_receita);

	if (ie_atualizar_w = 'S') then
		select	nr_dias_receita,
			dt_inicio_receita,
			dt_validade_receita
		into STRICT	nr_dias_receita_w,
			dt_inicio_receita_w,
			dt_validade_receita_w
		from	fa_receita_farmacia
		where	nr_sequencia = nr_seq_receita_p;

		update	fa_receita_farmacia_item
		set	nr_dias_receita		= nr_dias_receita_w,
			dt_inicio_receita	= dt_inicio_receita_w,
			dt_validade_receita	= dt_validade_receita_w,
			dt_atualizacao		= clock_timestamp(),
			nm_usuario		= nm_usuario_p
		where	nr_seq_receita 	= nr_seq_receita_p
		and	ie_uso_continuo = 'S';
	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
