CREATE OR REPLACE PROCEDURE fa_ajustar_val_dias_receita ( nr_seq_receita_p bigint, nr_seq_item_p bigint, nm_usuario_p text) AS $body$
DECLARE



nr_dias_receita_w 		integer;
nr_sequencia_w			bigint;
ie_atualizar_val_receita_w	varchar(1);
dt_validade_receita_w		timestamp;
dt_validade_real_receita_w	timestamp;


BEGIN

select 	nr_dias_receita,
	dt_validade_receita
into STRICT	nr_dias_receita_w,
	dt_validade_receita_w
from	fa_receita_farmacia b
where	nr_sequencia 		= nr_seq_receita_p;

CALL fa_consiste_validade_item(nr_seq_receita_p, nr_seq_item_p, clock_timestamp() + nr_dias_receita_w, nm_usuario_p);

select	max(a.nr_sequencia)
into STRICT	nr_sequencia_w
from	fa_receita_farmacia b,
 	fa_receita_farmacia_item a
where	a.nr_seq_receita 	 = nr_seq_receita_p
and	a.nr_seq_receita 	 = b.nr_sequencia
and	a.nr_sequencia 		 = nr_seq_item_p;

update 	fa_receita_farmacia_item
set	dt_validade_real_receita 	= clock_timestamp() + nr_dias_receita_w
where	nr_sequencia			= nr_sequencia_w;

CALL preparar_medic_dias_item(nr_seq_receita_p,  nr_seq_item_p, nm_usuario_p);

ie_atualizar_val_receita_w := Obter_Valor_Param_Usuario(10015, 49, obter_perfil_ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento);

if (ie_atualizar_val_receita_w = 'S') then

	select	max(dt_validade_real_receita)
	into STRICT	dt_validade_real_receita_w
	from	fa_receita_farmacia b,
		fa_receita_farmacia_item a
	where	a.nr_seq_receita 	 = nr_seq_receita_p
	and	a.nr_seq_receita 	 = b.nr_sequencia
	and	a.nr_sequencia 		 = nr_seq_item_p;

	if (dt_validade_real_receita_w > dt_validade_receita_w) then

		update fa_receita_farmacia
		set	dt_validade_receita 	= trunc(dt_validade_real_receita_w)
		where	nr_sequencia 		= nr_seq_receita_p;

	end if;

end if;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
