SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE fa_altera_agendamento_retorno (nr_seq_entrega_p bigint, nr_seq_agenda_p text) AS $body$
DECLARE


dt_agendamento_w	timestamp;
dt_periodo_final_w	timestamp;


BEGIN

if (nr_seq_agenda_p <> 0) and (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') then
	select	dt_agenda
	into STRICT	dt_agendamento_w
	from	agenda_consulta
	where	nr_sequencia = nr_seq_agenda_p;

	select 	dt_periodo_final
	into STRICT	dt_periodo_final_w
	from   	fa_entrega_medicacao
	where	nr_sequencia = nr_seq_entrega_p;

	if trunc(dt_agendamento_w) <= trunc(dt_periodo_final_w) then
		update	fa_entrega_medicacao
		set	dt_prevista_retorno = dt_agendamento_w
		where	nr_sequencia = nr_seq_entrega_p;
	else
		CALL WHEB_MENSAGEM_PCK.Exibir_Mensagem_Abort(279254, 'DT_AGENDAMENTO=' || to_char(dt_agendamento_w,'dd/mm/yyyy') || ';' || 'DT_PERIODO_FINAL=' || to_char(dt_periodo_final_w,'dd/mm/yyyy'));
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
