CREATE OR REPLACE PROCEDURE fa_altera_dt_inicio_rec ( nr_sequencia_p bigint, dt_inicio_p timestamp, nr_dias_p bigint default 0, ie_atualiza_medic_p text DEFAULT NULL) AS $body$
DECLARE

dt_validade_w		timestamp;
nr_dias_receita_w	integer;
nr_seq_farmacia_item_w	bigint;

c01 CURSOR FOR
	SELECT	nr_dias_receita,
		nr_sequencia
	from	fa_receita_farmacia_item
	where	nr_seq_receita = nr_sequencia_p;
	--and	nvl(ie_uso_continuo,'N') = 'N';
BEGIN
dt_validade_w := null;

if (dt_inicio_p IS NOT NULL AND dt_inicio_p::text <> '') then

	if (nr_dias_p > 0) then
		dt_validade_w := dt_inicio_p + trunc(nr_dias_p)-1;
	end if;
	if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
		update	fa_receita_farmacia
		set	dt_validade_receita = dt_validade_w,
			dt_inicio_receita = dt_inicio_p
		where	nr_sequencia = nr_sequencia_p;
	end if;

	if (ie_atualiza_medic_p = 'S') then
		open c01;
		loop
		fetch c01 into
			nr_dias_receita_w,
			nr_seq_farmacia_item_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
			begin
			dt_validade_w := null;
			if (coalesce(nr_dias_receita_w::text, '') = '') and (nr_dias_p > 0) then
				nr_dias_receita_w := nr_dias_p;
			end if;

			if (nr_dias_receita_w IS NOT NULL AND nr_dias_receita_w::text <> '') then
				dt_validade_w := dt_inicio_p + trunc(nr_dias_receita_w)-1;
			end if;

			update	fa_receita_farmacia_item
			set	dt_validade_receita = dt_validade_w,
				dt_inicio_receita   = dt_inicio_p,
				nr_dias_receita	    = nr_dias_receita_w
			where	nr_sequencia 	    = nr_seq_farmacia_item_w;
			end;
		end loop;
		close c01;
	end if;
end if;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
