SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE fa_atualizar_data_real_medic ( nr_seq_receita_p bigint, nm_usuario_p text) AS $body$
DECLARE




nr_sequencia_w		bigint;
qtd_w			integer;
ie_receita_externa_w	varchar(1);

C01 CURSOR FOR
	SELECT 	nr_sequencia
	from 	fa_receita_farmacia_item
	where 	nr_seq_receita 		= nr_seq_receita_p
	and 	ie_uso_continuo 	= 'N'
	order by nr_sequencia;


BEGIN

select	max(ie_receita_externa)
into STRICT	ie_receita_externa_w
from	fa_receita_farmacia
where	nr_sequencia = nr_seq_receita_p;


Select 	count(*)
into STRICT	qtd_w
from 	fa_receita_farmacia_item
where 	nr_seq_receita 	= nr_seq_receita_p
and 	ie_uso_continuo 	= 'N'
and 	((coalesce(dt_inicio_real_receita::text, '') = '') or (coalesce(dt_validade_real_receita::text, '') = ''));

if (qtd_w > 0) then
	open C01;
	loop
	fetch C01 into
		nr_sequencia_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		if (ie_receita_externa_w = 'S') then
			update	fa_receita_farmacia_item
			set	dt_inicio_real_receita 		= dt_inicio_receita,
				dt_validade_real_receita 	= dt_validade_receita
			where	nr_sequencia			= nr_sequencia_w;
		else
			update	fa_receita_farmacia_item
			set	dt_inicio_real_receita 		= clock_timestamp(),
				dt_validade_real_receita 	= (clock_timestamp() + coalesce(nr_dias_receita,0))-1
			where	nr_sequencia			= nr_sequencia_w;
		end if;

		CALL preparar_medic_dias_item(nr_seq_receita_p, nr_sequencia_w, nr_seq_receita_p);

		end;
	end loop;
	close C01;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
