CREATE OR REPLACE PROCEDURE fa_atualizar_medic_substituto ( nr_seq_entrega_p bigint, nr_seq_regra_p bigint, qt_subst_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE



cd_material_subst_w	bigint;
qt_gerada_subst_w	double precision;
cd_unidade_medida_w	varchar(30);
qt_dispensar_prin_w	double precision;


BEGIN

if (nr_seq_entrega_p IS NOT NULL AND nr_seq_entrega_p::text <> '') then

	select 	qt_dispensar
	into STRICT	qt_dispensar_prin_w
	from	fa_entrega_medicacao_item
	where	nr_sequencia	= nr_seq_entrega_p;

	select  cd_material_subst,
		qt_material * qt_dispensar_prin_w,
		cd_unidade_medida
	into STRICT	cd_material_subst_w,
		qt_gerada_subst_w,
		cd_unidade_medida_w
	from 	regra_mat_subst_atend
	where	nr_sequencia		= nr_seq_regra_p
	and	ie_situacao		= 'A'
	and	cd_estabelecimento 	= cd_estabelecimento_p;

	-- verificar se foi dispensada todo a quantidade gerada para o  substituto
	if (qt_gerada_subst_w <> qt_subst_p) then
		qt_dispensar_prin_w	:= (qt_dispensar_prin_w * qt_subst_p)/qt_gerada_subst_w;
	else
		qt_dispensar_prin_w	:= qt_subst_p;
	end if;

	update  fa_entrega_medicacao_item
	set	cd_material_subst 	= cd_material_subst_w,
		qt_material_subst	= qt_subst_p,
		cd_unidade_subst	= cd_unidade_medida_w,
		qt_dispensar		= qt_dispensar_prin_w,
		dt_atualizacao		= clock_timestamp(),
		nm_usuario		= nm_usuario_p
	where	nr_sequencia		= nr_seq_entrega_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
