CREATE OR REPLACE PROCEDURE fa_atualiza_datas_receita (nr_seq_receita_P bigint, dt_inicio_receita_p timestamp, dt_validade_receita_p timestamp, nm_usuario_p text) AS $body$
BEGIN

if (nr_seq_receita_p <> 0) then
	update	fa_receita_farmacia
	set	dt_validade_receita = dt_validade_receita_p,
		dt_inicio_receita   = dt_inicio_receita_p,
		nr_dias_receita     = (dt_validade_receita_p - dt_inicio_receita_p + 1)
	where	nr_sequencia = nr_seq_receita_p;

	CALL preparar_medic_dias(nr_seq_receita_P,nm_usuario_p);

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
