CREATE OR REPLACE PROCEDURE fa_atualiza_data_retorno_aut ( nr_seq_entrega_p bigint, cd_pessoa_fisica_p text) AS $body$
DECLARE


dt_validade_receita_w	timestamp;
dt_previsto_retorno_w	timestamp;


BEGIN
if (nr_seq_entrega_p IS NOT NULL AND nr_seq_entrega_p::text <> '') and (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	select	c.dt_validade_receita
	into STRICT	dt_validade_receita_w
	from   	fa_entrega_medicacao a,
		fa_receita_farmacia c
	where  	c.nr_sequencia     = a.nr_seq_receita_amb
	and    	a.cd_pessoa_fisica = cd_pessoa_fisica_p
	and    	a.nr_sequencia 	   = nr_seq_entrega_p;


	select min(b.dt_agenda)
	into STRICT	dt_previsto_retorno_w
	from   	fa_entrega_medicacao a,
		agenda_consulta b
	where  	a.nr_sequencia     = b.nr_seq_fa_entrega
	and    	a.cd_pessoa_fisica = cd_pessoa_fisica_p
	and    	a.nr_sequencia 	   <> nr_seq_entrega_p
	and    	a.dt_periodo_inicial between  inicio_dia(clock_timestamp()) and fim_dia(clock_timestamp())
	and    	b.dt_agenda <= dt_validade_receita_w;



	if (dt_previsto_retorno_w IS NOT NULL AND dt_previsto_retorno_w::text <> '') then
		update	fa_entrega_medicacao
		set	dt_prevista_retorno = dt_previsto_retorno_w
		where	nr_sequencia = nr_seq_entrega_p;
	end if;
end if;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
