CREATE OR REPLACE PROCEDURE fa_cancelar_entrega (nr_seq_entrega_p bigint, nr_seq_entr_item_p bigint, nr_seq_motivo_p bigint, nm_usuario_p text, ds_justificativa_p text default null) AS $body$
DECLARE

 
ie_status_medicacao_w	varchar(3);
nr_seq_entrega_item_w	bigint;
ie_cancela_entrega_w	varchar(1);

C01 CURSOR FOR 
	SELECT 	nr_sequencia 
	from	fa_entrega_medicacao_item 
	where	nr_seq_fa_entrega = nr_seq_entrega_p 
	and	coalesce(dt_cancelamento::text, '') = '';
			

BEGIN 
 
if (nr_seq_entrega_p <> 0) then 
 
	select	ie_status_medicacao 
	into STRICT	ie_status_medicacao_w 
	from	fa_entrega_medicacao 
	where	nr_sequencia = nr_seq_entrega_p;
	 
	if (ie_status_medicacao_w = 'EM') or (ie_status_medicacao_w = 'CM') then 
		--fa_gerar_estorno_entrega(nr_seq_entrega_p, nm_usuario_p); 
	 
		open C01;
		loop 
		fetch C01 into	 
			nr_seq_entrega_item_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
			begin 
			 
			CALL fa_gerar_estorno_entrega_item(nr_seq_entrega_item_w, nm_usuario_p);
 
			update 	fa_entrega_medicacao_item 
			set	nr_seq_motivo_canc = nr_seq_motivo_p, 
				nm_usuario_canc	=	nm_usuario_p, 
				nm_usuario	=	nm_usuario_p, 
				dt_cancelamento =	clock_timestamp(), 
				ds_justificativa = ds_justificativa_p 
			where	nr_sequencia = nr_seq_entrega_item_w;
			 
			commit;
			 
			end;
		end loop;
		close C01;
	 
	else 
	 
		update 	fa_entrega_medicacao_item 
		set	nr_seq_motivo_canc = nr_seq_motivo_p, 
			nm_usuario_canc	=	nm_usuario_p, 
			nm_usuario	=	nm_usuario_p, 
			dt_cancelamento =	clock_timestamp(), 
			ds_justificativa = ds_justificativa_p 
		where	nr_seq_fa_entrega = nr_seq_entrega_p 
		and	coalesce(nr_seq_motivo_canc::text, '') = '';
		 
	end if;
	 
	select	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END  
	into STRICT	ie_cancela_entrega_w 
	from	fa_entrega_medicacao_item 
	where	nr_seq_fa_entrega = nr_seq_entrega_p 
	and	coalesce(dt_cancelamento::text, '') = '';
	 
	if (ie_cancela_entrega_w = 'S') then 
		update 	fa_entrega_medicacao 
		set	nr_seq_motivo_canc = nr_seq_motivo_p, 
			nm_usuario_canc	=	nm_usuario_p, 
			nm_usuario	=	nm_usuario_p, 
			dt_cancelamento =	clock_timestamp(), 
			ds_justificativa = ds_justificativa_p 
		where	nr_sequencia = nr_seq_entrega_p;
	end if;
		 
elsif (nr_seq_entr_item_p > 0) then 
 
	select	ie_status_medicacao 
	into STRICT	ie_status_medicacao_w 
	from	fa_entrega_medicacao_item 
	where	nr_sequencia = nr_seq_entr_item_p;
 
	if (ie_status_medicacao_w = 'EM') or (ie_status_medicacao_w = 'CM') then 
		CALL fa_gerar_estorno_entrega_item(nr_seq_entr_item_p, nm_usuario_p);
	end if;
	 
	update 	fa_entrega_medicacao_item 
	set	nr_seq_motivo_canc = nr_seq_motivo_p, 
		nm_usuario_canc	=	nm_usuario_p, 
		nm_usuario	=	nm_usuario_p, 
		dt_cancelamento =	clock_timestamp(), 
		ds_justificativa = ds_justificativa_p 
	where	nr_sequencia = nr_seq_entr_item_p;	
	 
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
