CREATE OR REPLACE PROCEDURE fa_consiste_validade_item ( nr_seq_receita_p bigint, nr_seq_item_p bigint, dt_validade_p timestamp, nm_usuario_p text) AS $body$
DECLARE


dt_horario_max_entr_w	timestamp;


BEGIN

SELECT	MAX(b.dt_horario)
into STRICT	dt_horario_max_entr_w
FROM	fa_entr_medic_item_receita a,
	fa_receita_farm_item_hor b
WHERE	a.nr_seq_medic_item_hor = b.nr_sequencia
AND	b.nr_seq_fa_item 	= nr_seq_item_p;

if (dt_validade_p <= dt_horario_max_entr_w) then
	-- Não é possível alterar a data de validade pois já foram efetuadas entregas para datas superiores a esta. Favor verificar
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(263605);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
