CREATE OR REPLACE PROCEDURE fa_gravar_log_conferencia ( ie_tipo_p text, nr_seq_entrega_item_p bigint, ds_mensagem_p text, qt_dispensar_p text, nm_usuario_p text) AS $body$
DECLARE


nr_atendimento_w	bigint;
nr_seq_fa_entrega_w	bigint;
cd_material_w		bigint;


BEGIN

if (nr_seq_entrega_item_p IS NOT NULL AND nr_seq_entrega_item_p::text <> '') then
	select	nr_seq_fa_entrega,
		cd_material
	into STRICT	nr_seq_fa_entrega_w,
		cd_material_w
	from	fa_entrega_medicacao_item
	where	nr_sequencia = nr_seq_entrega_item_p;

	select 	max(a.nr_atendimento)
	into STRICT	nr_atendimento_w
	from   	fa_paciente_entrega a,
		fa_entrega_medicacao b
	where  	a.nr_sequencia = b.nr_seq_paciente_entrega
	and    	b.nr_sequencia = nr_seq_fa_entrega_w;

	insert into fa_log_conferencia(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_atendimento,
		cd_material,
		ds_mensagem,
		ie_tipo,
		nr_seq_item,
		qt_dispensar,
		ie_situacao)
	values (	nextval('fa_log_conferencia_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_atendimento_w,
		cd_material_w,
		ds_mensagem_p,
		ie_tipo_p,
		nr_seq_entrega_item_p,
		qt_dispensar_p,
		'A');
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
