CREATE OR REPLACE PROCEDURE fa_inserir_medicamento_pmc ( nr_sequencia_p bigint, ds_lista_medic_p text, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w	bigint;
nr_seq_pac_pm_w	bigint;


nr_seq_medic_fa_w	bigint;
ds_lista_w	varchar(1000);
tam_lista_w	bigint;
ie_pos_virgula_w	smallint;


BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (ds_lista_medic_p IS NOT NULL AND ds_lista_medic_p::text <> '') then
	ds_lista_w 	:= ds_lista_medic_p;
	nr_sequencia_w 	:= nr_sequencia_p;




	while(ds_lista_w IS NOT NULL AND ds_lista_w::text <> '') loop
		begin
		tam_lista_w		:= length(ds_lista_w);
		ie_pos_virgula_w		:= position(',' in ds_lista_w);
		if (ie_pos_virgula_w <> 0) then
			nr_seq_medic_fa_w	:= (substr(ds_lista_w,1,(ie_pos_virgula_w - 1)))::numeric;
			ds_lista_w	:= substr(ds_lista_w,(ie_pos_virgula_w + 1),tam_lista_w);
		end if;

		select	nextval('fa_paciente_pmc_medic_seq')
		into STRICT	nr_seq_pac_pm_w
		;

		if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then
			begin


			insert into fa_paciente_pmc_medic(	nr_sequencia,
							dt_atualizacao,
							nm_usuario,
							dt_atualizacao_nrec,
							nm_usuario_nrec,
							nr_seq_medic_fa,
							nr_seq_fa_pmc)
						values (nr_seq_pac_pm_w,
							clock_timestamp(),
							nm_usuario_p,
							clock_timestamp(),
							nm_usuario_p,
							nr_seq_medic_fa_w,
							nr_sequencia_w);
			end;
		end if;
		end;
	end loop;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
