SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE fa_inserir_pac_pmc (cd_pessoa_fisica_p text, ds_lista_medic_p text, nr_seq_entrega_p bigint, nm_usuario_p text, ie_tipo_p text) AS $body$
DECLARE


nr_sequencia_w	bigint;	
nr_seq_pac_pm_w	bigint;
nr_seq_pmc_w	bigint;

nr_seq_medic_fa_w	bigint;
ds_lista_w		varchar(1000);
tam_lista_w		bigint;
ie_pos_virgula_w	smallint;
cd_material_w	material.cd_material%type;	
			     

BEGIN

update fa_entrega_medicacao_item
set ie_medicamento_pmc = 'N'
where nr_seq_fa_entrega = nr_seq_entrega_p;

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and (ds_lista_medic_p IS NOT NULL AND ds_lista_medic_p::text <> '') then

	ds_lista_w := ds_lista_medic_p;
	
	select	max(nr_sequencia)
	into STRICT	nr_seq_pmc_w
	from	fa_paciente_pmc
	where	cd_pessoa_fisica = cd_pessoa_fisica_p
	and	substr(fa_obter_tipo_entrega_pac(nr_sequencia),1,3) = ie_tipo_p
	and	coalesce(dt_saida::text, '') = '';
		
	
	if (coalesce(nr_seq_pmc_w::text, '') = '') then
	
		select	nextval('fa_paciente_pmc_seq')
		into STRICT	nr_sequencia_w
		;
	
		insert into fa_paciente_pmc(nr_sequencia,
					dt_atualizacao,         
					nm_usuario,             
					dt_atualizacao_nrec,    
					nm_usuario_nrec,        
					cd_pessoa_fisica,
					nr_entrega_origem_pmc,
					cd_estabelecimento)
				values (nr_sequencia_w,
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p,
					cd_pessoa_fisica_p,
					nr_seq_entrega_p,
					wheb_usuario_pck.get_cd_estabelecimento);
	end if;
					
	while(ds_lista_w IS NOT NULL AND ds_lista_w::text <> '')  loop
		begin
		
		tam_lista_w		:= length(ds_lista_w);
		ie_pos_virgula_w		:= position(',' in ds_lista_w);
	
		if (ie_pos_virgula_w <> 0) then
			nr_seq_medic_fa_w	:= (substr(ds_lista_w,1,(ie_pos_virgula_w - 1)))::numeric;
			ds_lista_w		:= substr(ds_lista_w,(ie_pos_virgula_w + 1),tam_lista_w);
		end if;
				
		if (nr_seq_medic_fa_w IS NOT NULL AND nr_seq_medic_fa_w::text <> '') then
		
			select max(cd_material)
				into STRICT cd_material_w
			from fa_medic_farmacia_amb
			where nr_sequencia = nr_seq_medic_fa_w;	

			update fa_entrega_medicacao_item
			set ie_medicamento_pmc = 'S'
			where cd_material = cd_material_w
			and   nr_seq_fa_entrega = nr_seq_entrega_p
			and coalesce(ie_medicamento_pmc,'N') <> 'S'  LIMIT 1;
		end if;
				
		select	nextval('fa_paciente_pmc_medic_seq')
		into STRICT	nr_seq_pac_pm_w
		;
		
		if (coalesce(nr_seq_pmc_w::text, '') = '') then
			insert into fa_paciente_pmc_medic(nr_sequencia,
							dt_atualizacao,         
							nm_usuario,             
							dt_atualizacao_nrec,    
							nm_usuario_nrec,        
							nr_seq_medic_fa,        
							nr_seq_fa_pmc)
						values (nr_seq_pac_pm_w,
							clock_timestamp(),
							nm_usuario_p,
							clock_timestamp(),
							nm_usuario_p,
							nr_seq_medic_fa_w,
							nr_sequencia_w);
		elsif (nr_seq_pmc_w IS NOT NULL AND nr_seq_pmc_w::text <> '') then
			insert into fa_paciente_pmc_medic(nr_sequencia,
							dt_atualizacao,         
							nm_usuario,             
							dt_atualizacao_nrec,    
							nm_usuario_nrec,        
							nr_seq_medic_fa,        
							nr_seq_fa_pmc)
						values (nr_seq_pac_pm_w,
							clock_timestamp(),
							nm_usuario_p,
							clock_timestamp(),
							nm_usuario_p,
							nr_seq_medic_fa_w,
							nr_seq_pmc_w);
		end if;
		end;
		end loop;
				

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE fa_inserir_pac_pmc (cd_pessoa_fisica_p text, ds_lista_medic_p text, nr_seq_entrega_p bigint, nm_usuario_p text, ie_tipo_p text) FROM PUBLIC;

