CREATE OR REPLACE PROCEDURE fa_liberar_receita_ccih ( nr_seq_medic_p bigint, nm_usuario_p text) AS $body$
BEGIN

begin
	if (nr_seq_medic_p IS NOT NULL AND nr_seq_medic_p::text <> '') then
		update 	fa_receita_farmacia_item
		set	dt_liberacao_cih = clock_timestamp(),
			nm_usuario_lib_cih = nm_usuario_p,
			dt_atualizacao = clock_timestamp(),
			nm_usuario   = nm_usuario_p
		where	nr_sequencia = nr_seq_medic_p;
	end if;
commit;
exception
when others then
	-- Erro ao liberar a receita pelo CCIH
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(263616);
end;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
