CREATE OR REPLACE PROCEDURE fa_local_estoque_item_sem_val ( nm_computador_p text, nr_seq_receita_p bigint, cd_local_estoque_p INOUT bigint, qt_itens_sem_validade_p INOUT bigint) AS $body$
DECLARE


cd_local_estoque_w	integer;
qt_itens_sem_validade_w bigint;


BEGIN
	select	coalesce(max(cd_local_estoque),0)
	into STRICT 	cd_local_estoque_w
	from	local_estoque_computador b,
		computador a
	where	a.nr_sequencia		= b.nr_seq_computador
	and	a.nm_computador_pesquisa 	= padronizar_nome(upper(nm_computador_p));

	select	count(*)
	into STRICT 	qt_itens_sem_validade_w
	from	fa_receita_farmacia_item
	where	nr_seq_receita	= nr_seq_receita_p
	and	ie_uso_continuo = 'N'
	and	((coalesce(dt_inicio_real_receita::text, '') = '') or (coalesce(dt_validade_real_receita::text, '') = ''));

cd_local_estoque_p	:= cd_local_estoque_w;
qt_itens_sem_validade_p	:= qt_itens_sem_validade_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
