CREATE OR REPLACE PROCEDURE fa_registrar_saida_pmc ( nr_seq_paciente_pmc_p bigint, nr_seq_motivo_p bigint, ds_justificativa_p text, nm_usuario_p text) AS $body$
BEGIN

if (nr_seq_paciente_pmc_p IS NOT NULL AND nr_seq_paciente_pmc_p::text <> '') then

	update 	fa_paciente_pmc
	set	dt_saida		= clock_timestamp(),
		nr_seq_motivo_saida	= nr_seq_motivo_p,
		ds_observacao		= ds_justificativa_p,
		nm_usuario_saida	= nm_usuario_p,
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_sequencia		= nr_seq_paciente_pmc_p;

end if;

commit;

end;
$body$
