SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE fechar_lote_envio ( nr_seq_lote_p text, nm_usuario_p text) AS $body$
DECLARE


ds_historico_w		varchar(255);
dt_aprovacao_w		timestamp;


BEGIN

if (nr_seq_lote_p IS NOT NULL AND nr_seq_lote_p::text <> '')then

	select 	dt_aprovacao
	into STRICT	dt_aprovacao_w
	from	lote_orgao_cobranca
	where	nr_sequencia = nr_seq_lote_p;

	if (coalesce(dt_aprovacao_w::text, '') = '') then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(674997);
	end if;

	update lote_orgao_cobranca
	set dt_fechamento = clock_timestamp()
	where nr_sequencia = nr_seq_lote_p;

	ds_historico_w := wheb_mensagem_pck.get_texto(337822, 'NM_USUARIO=' || nm_usuario_p || ';DT_ATUALIZACAO=' || to_char(clock_timestamp(),'dd/MM/yyyy hh24:mi:ss'));
	CALL gerar_historico_cobr_orgao(null,null,nr_seq_lote_p,ds_historico_w,nm_usuario_p,'E');

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
