SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE fechar_lote_fluxo_caixa ( nr_seq_lote_fluxo_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_classif_fluxo_w	fluxo_caixa_lote.ie_classif_fluxo%type;
dt_referencia_w		timestamp;
qt_reg_ant_w		bigint	:= 0;
ds_historico_w		fluxo_caixa_lote_hist.ds_historico%type;
ie_restringe_estab_w	fluxo_caixa_lote.ie_restringe_estab%type;
cd_estabelecimento_w	estabelecimento.cd_estabelecimento%type;
cd_empresa_w			estabelecimento.cd_empresa%type;


BEGIN

select	max(a.ie_classif_fluxo),
		max(a.dt_mesano_referencia),
		max(a.ie_restringe_estab)
into STRICT	ie_classif_fluxo_w,
		dt_referencia_w,
		ie_restringe_estab_w
from	fluxo_caixa_lote a
where	a.nr_sequencia 	= nr_seq_lote_fluxo_p;

/*OS 1501187 - Peguei o ativo para nao precisar alterar no fonte do java. No fonte passaria o estabelecimento logado, que é o ativo da mesma forma*/

cd_estabelecimento_w := obter_estabelecimento_ativo;

if (cd_estabelecimento_w IS NOT NULL AND cd_estabelecimento_w::text <> '') then
	select	max(cd_empresa)
	into STRICT	cd_empresa_w
	from	estabelecimento
	where	cd_estabelecimento = cd_estabelecimento_w;
end if;

/* não permitir fechar se o lote do mês anterior ainda não estiver fechado */

select	count(*)
into STRICT	qt_reg_ant_w
from	fluxo_caixa_lote a
where	coalesce(a.dt_fechamento::text, '') = ''
and		a.ie_classif_fluxo	= ie_classif_fluxo_w
and		a.dt_mesano_referencia	< dt_referencia_w
and		a.cd_empresa	= cd_empresa_w
and	( (ie_restringe_estab_w = 'N') or (ie_restringe_estab_w = 'E' and a.cd_estabelecimento = cd_estabelecimento_w) or (ie_restringe_estab_w = 'F' and (a.cd_estabelecimento = cd_estabelecimento_w or obter_estab_financeiro(cd_estabelecimento_w) = a.cd_estabelecimento)) );

if (qt_reg_ant_w = 0)then
	update	fluxo_caixa_lote
	set	dt_fechamento	= clock_timestamp(),
		dt_atualizacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p
	where	nr_sequencia	= nr_seq_lote_fluxo_p;
else
	/* Não é possível fechar o lote pois o lote do mês anterior não está fechado! */

	CALL wheb_mensagem_pck.exibir_mensagem_abort(290967);
end if;

/* Fechamento do fluxo de caixa lote */

ds_historico_w	:= substr(wheb_mensagem_pck.get_texto(290757),1,4000);

CALL GERAR_HISTORICO_FLUXO_LOTE(	nr_seq_lote_fluxo_p,
				'S',
				ds_historico_w,
				nm_usuario_p);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
