SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE fechar_pedido_far_com (nr_seq_pedido_p bigint) AS $body$
DECLARE


vl_total_w		double precision;
vl_pagamento_w		double precision;
vl_troco_w		double precision := 0;


BEGIN

select	sum(vl_pagamento)
into STRICT	vl_pagamento_w
from	far_pedido_forma_pagto
where	nr_seq_pedido = nr_seq_pedido_p;

select	vl_total
into STRICT	vl_total_w
from	far_pedido
where	nr_sequencia = nr_seq_pedido_p;

if (vl_pagamento_w > vl_total_w) then
	vl_troco_w	:= (vl_pagamento_w - vl_total_w);
end if;

update	far_pedido
set	dt_fechamento = clock_timestamp(),
	vl_troco = vl_troco_w,
	ie_status_pedido = 'P'
where	nr_sequencia = nr_seq_pedido_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
