CREATE OR REPLACE PROCEDURE fechar_saldo_diario_banco ( nr_seq_banco_saldo_p bigint, dt_referencia_p timestamp, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_conta_banco_w	banco_saldo.nr_seq_conta%type;
dt_inicial_w		timestamp;
dt_final_w		timestamp;
dt_fechamento_w		fechamento_banco.dt_fechamento%type;
vl_saldo_emprestimo_w	fechamento_banco.vl_saldo_emprestimo%type;
vl_emprestimo_w		movto_trans_financ.vl_transacao%type;


BEGIN

CALL gerar_lancamento_banco(nr_seq_banco_saldo_p,nm_usuario_p,'N','DB',dt_referencia_p);

select	max(a.nr_seq_conta)
into STRICT	nr_seq_conta_banco_w
from	banco_saldo a
where	a.nr_sequencia = nr_seq_banco_saldo_p;

/* atualizar o saldo de empréstimo */

select	max(a.dt_fechamento)
into STRICT	dt_fechamento_w
from	fechamento_banco a
where	a.nr_seq_conta_banco	= nr_seq_conta_banco_w
and	coalesce(a.cd_estabelecimento,coalesce(cd_estabelecimento_p,0))	= coalesce(cd_estabelecimento_p,0);

select	max(a.vl_saldo_emprestimo)
into STRICT	vl_saldo_emprestimo_w
from	fechamento_banco a
where	a.dt_fechamento		= dt_fechamento_w
and	a.nr_seq_conta_banco	= nr_seq_conta_banco_w
and	coalesce(a.cd_estabelecimento,coalesce(cd_estabelecimento_p,0))	= coalesce(cd_estabelecimento_p,0);

dt_inicial_w	:= trunc(dt_referencia_p,'dd');
dt_final_w	:= fim_dia(dt_referencia_p);

select	sum(CASE WHEN coalesce(b.ie_emprestimo,'N')='S' THEN a.vl_transacao WHEN coalesce(b.ie_emprestimo,'N')='D' THEN a.vl_transacao * -1  ELSE 0 END )
into STRICT	vl_emprestimo_w
from	transacao_financeira b,
	movto_trans_financ a
where	a.nr_seq_trans_financ	= b.nr_sequencia
and	a.dt_transacao		between dt_inicial_w and dt_final_w
and	a.nr_seq_saldo_banco	= nr_seq_banco_saldo_p;

/* FIM - atualizar o saldo de empréstimo */

insert into fechamento_banco(
	nr_sequencia,
	nm_usuario,
	nm_usuario_nrec,
	dt_atualizacao,
	dt_atualizacao_nrec,
	nr_seq_conta_banco,
	dt_fechamento,
	cd_estabelecimento,
	vl_saldo_emprestimo)
values (nextval('fechamento_banco_seq'),
	nm_usuario_p,
	nm_usuario_p,
	clock_timestamp(),
	clock_timestamp(),
	nr_seq_conta_banco_w,
	dt_referencia_p,
	cd_estabelecimento_p,
	coalesce(vl_saldo_emprestimo_w,0) + coalesce(vl_emprestimo_w,0));

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
