CREATE OR REPLACE PROCEDURE fechar_setor_temperatura ( nr_sequencia_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
cd_setor_atendimento_w		bigint;
ds_erro_w			varchar(4000);
nr_seq_turno_w			bigint;
qt_temp_inicial_w		setor_temperatura.qt_temp_inicial%type;
qt_temp_min_w			setor_temperatura.qt_temp_min%type;
qt_temp_max_w			setor_temperatura.qt_temp_max%type;
qt_umidade_final_w		setor_temperatura.qt_umidade_final%type;
qt_temp_min_item_w		setor_temperatura_item.qt_temp_min%type;
qt_temp_max_item_w		setor_temperatura_item.qt_temp_max%type;
Ie_AtualizaTempMinMAx_w		varchar(1);
ds_observacao_w			varchar(4000);


BEGIN 
Ie_AtualizaTempMinMAx_w := obter_valor_param_usuario(1300, 20, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento);
 
/* Obter os dados do Registro de Temperatura */
 
select	cd_setor_atendimento, 
	nr_seq_turno, 
	qt_temp_inicial, 
	qt_temp_min, 
	qt_temp_max, 
	qt_umidade_final, 
	ds_observacao 
into STRICT	cd_setor_atendimento_w, 
	nr_seq_turno_w, 
	qt_temp_inicial_w, 
	qt_temp_min_w, 
	qt_temp_max_w, 
	qt_umidade_final_w, 
	ds_observacao_w 
from	setor_temperatura 
where	nr_sequencia	= nr_sequencia_p;
 
/* Fecha o turno */
 
update	setor_temperatura 
set	dt_hora_fim		= coalesce(dt_hora_fim,clock_timestamp()), 
	nm_usuario_fechamento	= nm_usuario_p 
where	nr_sequencia		= nr_sequencia_p;
 
/* Atualizar temperatura automáticamente se parâmetro [20] = 'S'*/
 
if (coalesce(Ie_AtualizaTempMinMAx_w,'N') = 'S') then 
	begin 
	select	min(qt_temp_min), 
		max(qt_temp_max) 
	into STRICT	qt_temp_min_item_w, 
		qt_temp_max_item_w 
	from	setor_temperatura_item 
	where	nr_seq_setor_temp = nr_sequencia_p;
	 
	update	setor_temperatura 
	set	qt_temp_min	= qt_temp_min_item_w, 
		qt_temp_max	= qt_temp_max_item_w 
	where	nr_sequencia	= nr_sequencia_p;
	end;
end if;
/* Consiste a temperatura do setor */
 
 
ds_erro_w := consiste_temperatura_setor(	cd_setor_atendimento_w, nr_seq_turno_w, qt_temp_inicial_W, qt_temp_min_w, qt_temp_max_w, qt_umidade_final_w, ds_erro_w, nm_usuario_p, cd_estabelecimento_p, 'S', ds_observacao_w);
				 
/* Consiste a temperatura dos Itens do setor-turno */
 
CALL consiste_itens_temp_setor(	nr_sequencia_p, cd_estabelecimento_p, nm_usuario_p);
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
