SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ffr_calcular_report ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_mes_ref_w	ffr_report.nr_seq_mes_ref%type;
cd_conta_contabil_w	ctb_saldo.cd_conta_contabil%type;
cd_centro_custo_w	ctb_saldo.cd_centro_custo%type;
vl_movimento_w		ctb_saldo.vl_movimento%type;
vl_saldo_w		ctb_saldo.vl_saldo%type;
dt_referencia_w		ctb_mes_ref.dt_referencia%type;
nr_seq_analisys_code_w	ffr_analisys_code_rateio.nr_seq_analisys_code%type;
nr_seq_function_area_w	ffr_function_area_rateio.nr_seq_function_area%type;

c01 CURSOR FOR
SELECT	c.cd_conta_contabil,
	c.cd_centro_custo,
	c.vl_movimento,
	c.vl_saldo
from	ctb_saldo c
where	c.nr_seq_mes_ref = nr_seq_mes_ref_w
and	(c.cd_centro_custo IS NOT NULL AND c.cd_centro_custo::text <> '')
and (substr(obter_se_conta_vigente(c.cd_conta_contabil, ctb_obter_mes_ref(nr_seq_mes_ref_w)),1,1) = 'S');

c02 CURSOR FOR
SELECT	f.nr_seq_analisys_code
from	ffr_analisys_code_rateio f
where	obter_se_periodo_vigente(f.dt_inicio_vigencia, f.dt_fim_vigencia, dt_referencia_w) = 'S'
and	f.cd_conta_contabil = cd_conta_contabil_w;

c03 CURSOR FOR
SELECT	f.nr_seq_function_area
from	ffr_function_area_rateio f
where	obter_se_periodo_vigente(f.dt_inicio_vigencia, f.dt_fim_vigencia, dt_referencia_w) = 'S'
and	f.cd_conta_contabil = cd_conta_contabil_w;


BEGIN

select	f.nr_seq_mes_ref
into STRICT	nr_seq_mes_ref_w
from	ffr_report f
where	f.nr_sequencia = nr_sequencia_p;

select	c.dt_referencia
into STRICT	dt_referencia_w
from	ctb_mes_ref c
where	c.nr_sequencia = nr_seq_mes_ref_w;

OPEN C01;
LOOP
FETCH C01 into
	cd_conta_contabil_w,
	cd_centro_custo_w,
	vl_movimento_w,
	vl_saldo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	OPEN c02;
	LOOP
	FETCH c02 into
		nr_seq_analisys_code_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */
		begin

		insert into ffr_report_movto(nr_sequencia,
			cd_centro_custo,
			nr_seq_report,
			nr_seq_function_area,
			nr_seq_analisys_code,
			cd_conta_contabil,
			vl_saldo,
			dt_atualizacao,
			nm_usuario)
		values (nextval('ffr_report_movto_seq'),
			cd_centro_custo_w,
			nr_sequencia_p,
			null,
			nr_seq_analisys_code_w,
			cd_conta_contabil_w,
			vl_saldo_w,
			clock_timestamp(),
			nm_usuario_p);
		end;

	END LOOP;
	CLOSE c02;


	OPEN c03;
	LOOP
	FETCH c03 into
		nr_seq_function_area_w;
	EXIT WHEN NOT FOUND; /* apply on c03 */
		begin

		insert into ffr_report_movto(nr_sequencia,
			cd_centro_custo,
			nr_seq_report,
			nr_seq_function_area,
			nr_seq_analisys_code,
			cd_conta_contabil,
			vl_saldo,
			dt_atualizacao,
			nm_usuario)
		values (nextval('ffr_report_movto_seq'),
			cd_centro_custo_w,
			nr_sequencia_p,
			nr_seq_function_area_w,
			null,
			cd_conta_contabil_w,
			vl_saldo_w,
			clock_timestamp(),
			nm_usuario_p);
		end;

	END LOOP;
	CLOSE c03;

	end;
END LOOP;
CLOSE C01;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
