CREATE OR REPLACE PROCEDURE ficha_financ_altera_macro ( ds_macro_antiga text, ds_macro_nova text) AS $body$
DECLARE


ds_sql_w		varchar(4000);
ds_macro_w		varchar(50);
ds_formula_w		varchar(4000);
ds_formula_ww		varchar(4000);
nr_sequencia_w		bigint;

c01 CURSOR FOR
SELECT	a.nr_sequencia,
	a.ds_formula
from	ficha_financ_formula a
order by	a.nr_sequencia;


BEGIN

open C01;
loop
fetch C01 into
	nr_sequencia_w,
	ds_formula_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ds_formula_ww := replace(ds_formula_w, '#A_'||ds_macro_antiga||'@', '#A_'||ds_macro_nova||'@');
	if (ds_formula_w <> ds_formula_ww) then
		update	ficha_financ_formula
		set	ds_formula = ds_formula_ww
		where 	nr_sequencia = nr_sequencia_w;
	end if;
	end;
end loop;
close C01;

end;
$body$
