CREATE OR REPLACE PROCEDURE finalizar_aferese (nr_seq_aferese_p bigint, nm_usuario_p text) AS $body$
BEGIN

if (nr_seq_aferese_p IS NOT NULL AND nr_seq_aferese_p::text <> '') then

	update  san_solic_aferese_terap
	set 	ie_status = 'FA'
	where 	nr_sequencia = nr_seq_aferese_p;

	update 	san_registro_aferese
	set 	nm_usuario_fim   = nm_usuario_p,
		nm_usuario  = nm_usuario_p,
		dt_final  = clock_timestamp(),
		dt_atualizacao  = clock_timestamp()
	where 	nr_seq_aferese_p = nr_seq_aferese_p;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
