SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE finalizar_alteracao_status_js (nr_prescricao_p bigint, ie_status_atend_p bigint, ie_status_atual_p bigint, ds_sql_item_p text) AS $body$
DECLARE

 
ds_sep_bv_w		varchar(50);
ds_comando_w		varchar(2000);
ds_lista_sequencia_w	varchar(2000);
lista_informacao_w	varchar(2000);
nr_sequencia_w		bigint;
tam_lista_w		bigint;
ie_pos_virgula_w	smallint;
dt_resultado_w		timestamp;
dt_resultado_prescr_w	timestamp;
dt_entrega_prescr_w	timestamp;
					

BEGIN 
 
ds_sep_bv_w	:= obter_separador_bv;
 
ds_comando_w :=	' select	nr_sequencia ' || 
		' from 		prescr_procedimento c '|| 
		' where 	nr_prescricao = :nr_prescricao '|| 
		' and 		obter_se_primeira_coleta(nr_sequencia, nr_prescricao) = '||'N'|| 
		' and 		ie_status_atend = :ie_status_atend '|| 
		' :ds_sql_item ';
 
ds_lista_sequencia_w := substr(Obter_select_concatenado_bv(ds_comando_w,'nr_prescricao=' || to_char(nr_prescricao_p) || ds_sep_bv_w || 
									'ie_status_atend=' || to_char(ie_status_atend_p) || ds_sep_bv_w || 
									'ds_sql_item=' || ds_sql_item_p || ds_sep_bv_w,','),1,2000);				
					 
lista_informacao_w	:= ds_lista_sequencia_w;
 
while(lista_informacao_w IS NOT NULL AND lista_informacao_w::text <> '') loop 
	begin 
	 
	tam_lista_w			:= length(lista_informacao_w);
	ie_pos_virgula_w		:= position(',' in lista_informacao_w);
 
	if (ie_pos_virgula_w <> 0) then 
		nr_sequencia_w		:= substr(lista_informacao_w,1,(ie_pos_virgula_w - 1));
		lista_informacao_w	:= substr(lista_informacao_w,(ie_pos_virgula_w + 1),tam_lista_w);
	end if;		
	 
	dt_resultado_w	:= obter_data_resultado_regra(nr_sequencia_w, nr_prescricao_p);
    
	if (dt_resultado_w IS NOT NULL AND dt_resultado_w::text <> '') then 
 
		update 	prescr_procedimento c 
		set 	dt_resultado 	= dt_resultado_w 
		where 	nr_prescricao 	= nr_prescricao_p 
		and 	nr_sequencia 	= nr_sequencia_w;
		 
		 
		select	max(dt_resultado) 
		into STRICT	dt_resultado_prescr_w 
		from 	prescr_procedimento 
		where 	nr_prescricao = nr_prescricao_p;
		 
		select 	max(dt_entrega) 
		into STRICT	dt_entrega_prescr_w 
		from 	prescr_medica 
		where 	nr_prescricao = nr_prescricao_p;
		 
		if (dt_resultado_prescr_w > dt_entrega_prescr_w) then 
			 
			update prescr_medica c 
			set dt_entrega 		= dt_resultado_prescr_w 
			where nr_prescricao 	= nr_prescricao_p;
		end if;
	end if;
	end;
end loop;	
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
