CREATE OR REPLACE PROCEDURE finalizar_atendimento_agenda ( nr_atendimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

			
			
qt_agenda_w		bigint;
cd_agenda_w		bigint;
nr_seq_agenda_w		    agenda_consulta.nr_sequencia%type;
dt_consulta_agenda_w	timestamp;
dt_atendido_w		timestamp;		
ie_gerar_lanc_aut_exec_w varchar(10);
ie_classif_agenda_w	varchar(5);


BEGIN

if (nr_atendimento_p > 0) then

	select 	max(cd_agenda),
		max(dt_consulta),
		max(dt_atendido),
		max(nr_sequencia),
		max(ie_classif_agenda)
	into STRICT	cd_agenda_w,
		dt_consulta_agenda_w,
		dt_atendido_w,
		nr_seq_agenda_w,
		ie_classif_agenda_w
	from	agenda_consulta
	where	nr_atendimento = nr_atendimento_p
	and	dt_agenda = (SELECT min(dt_agenda) from agenda_consulta where nr_atendimento = nr_atendimento_p and coalesce(dt_atendido::text, '') = '')
	and	coalesce(dt_atendido::text, '') = '';

	if (nr_seq_agenda_w > 0) then

		select	coalesce(max(a.ie_lanc_aut_exec),'N')
		into STRICT	ie_gerar_lanc_aut_exec_w
		from	agenda a,
			agenda_consulta b
		where 	a.cd_agenda	= b.cd_agenda
		and	b.nr_sequencia	= nr_seq_agenda_w;

		update	agenda_consulta
		set	ie_status_agenda	= 'E',
			dt_atendido		= clock_timestamp(),
			nm_usuario_atendido	= nm_usuario_p,
			nm_usuario		= nm_usuario_p
		where	nr_sequencia		= nr_seq_agenda_w;

		CALL executar_evento_agenda_atend(nr_atendimento_p,'SAP',obter_estab_atend(nr_atendimento_p),nm_usuario_p);

		if (ie_gerar_lanc_aut_exec_w	= 'S') and (coalesce(nr_atendimento_p,0) > 0) then
			CALL gerar_lancamento_automatico(nr_atendimento_p, null, 539, nm_usuario_p, nr_seq_agenda_w, null, null, null, ie_classif_agenda_w, null);
		end if;

	end if;
	
end if;	

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
