SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE finalizar_atend_precaucao ( nr_atendimento_p atendimento_paciente.nr_atendimento%type, dt_final_p timestamp, nr_seq_atend_precaucao_p atendimento_precaucao.nr_sequencia%type default null, ie_termino_p text default null, nm_usuario_p text DEFAULT NULL) AS $body$
DECLARE


nr_sequencia_w		bigint := 0;
ie_valor_parametro_w	varchar(1);


BEGIN

ie_valor_parametro_w := Obter_Param_Usuario(44, 244, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_valor_parametro_w);

if (coalesce(nr_seq_atend_precaucao_p,0) = 0) then
	select 	max(nr_sequencia)
	into STRICT	nr_sequencia_w
	from	atendimento_precaucao
	where	nr_atendimento = nr_atendimento_p
	and	coalesce(dt_termino::text, '') = '';
else
	nr_sequencia_w	:=  nr_seq_atend_precaucao_p;
end if;


if (nr_sequencia_w > 0) and
	((coalesce(ie_termino_p,'U') = 'U') or (coalesce(ie_valor_parametro_w,'U') = 'U')) then

	update	atendimento_precaucao
	set	dt_termino	= coalesce(dt_final_p,dt_final_precaucao,clock_timestamp())
	where	nr_sequencia	= nr_sequencia_w;

end if;

if (coalesce(ie_termino_p,'U') = 'T') or (coalesce(ie_valor_parametro_w,'U') = 'T')	then

	update	atendimento_precaucao
	set	dt_termino		= coalesce(dt_final_p,dt_final_precaucao,clock_timestamp()),
		dt_final_precaucao	= coalesce(dt_final_p,dt_final_precaucao,clock_timestamp()),
		nm_usuario_lib_iso	= nm_usuario_p,
		nm_usuario		= nm_usuario_p,
		dt_atualizacao 		=  clock_timestamp()
	where	nr_atendimento		= nr_atendimento_p
	and	((coalesce(dt_final_precaucao::text, '') = '') or (coalesce(dt_termino::text, '') = ''));

end if;

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
