CREATE OR REPLACE PROCEDURE finalizar_atend_senha (nr_atendimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_pac_senha_fila_w bigint;
				

BEGIN

if (coalesce(nr_atendimento_p,0) > 0) then

	SELECT	coalesce(MAX(a.nr_seq_pac_senha_fila),0)
	into STRICT nr_seq_pac_senha_fila_w		   
	FROM atendimento_pa_js_v a
	WHERE a.nr_atendimento = nr_atendimento_p;
	
end if;

if (coalesce(nr_atendimento_p,0) > 0) and (not nr_seq_pac_senha_fila_w > 0) then

	SELECT	coalesce(MAX(b.nr_seq_pac_senha_fila),0)
	into STRICT nr_seq_pac_senha_fila_w		
	FROM agenda_consulta_v2 b
	WHERE b.nr_atendimento = nr_atendimento_p;

end if;	
	
if (coalesce(nr_seq_pac_senha_fila_w, 0) > 0) then

	update	paciente_senha_fila
	set	dt_fim_atendimento	=	clock_timestamp(),
		nm_usuario		=	nm_usuario_p,
		dt_atualizacao		=	clock_timestamp()
	where	nr_sequencia 		=	nr_seq_pac_senha_fila_w;
	
	commit;
	
end if;	
	
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
