CREATE OR REPLACE PROCEDURE finalizar_cih_controle_atend (nr_seq_controle_p bigint, nr_atendimento_p bigint) AS $body$
DECLARE


qt_controle_w			bigint := 0;
ie_finaliza_controle_w	varchar(1);


BEGIN

ie_finaliza_controle_w := obter_param_usuario(7043, 30, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_finaliza_controle_w);

update	cih_controle_atend
set	dt_finalizacao 	= clock_timestamp()
where	nr_seq_controle = nr_seq_controle_p
and	nr_atendimento 	= nr_atendimento_p
and	coalesce(dt_finalizacao::text, '') = '';

select 	count(*)
into STRICT	qt_controle_w
from	cih_controle_atend
where	nr_seq_controle = nr_seq_controle_p
and	coalesce(dt_finalizacao::text, '') = '';

if (qt_controle_w <= 0) and (ie_finaliza_controle_w = 'S') then

	update	cih_controle
	set	dt_finalizacao = clock_timestamp()
	where	nr_sequencia = nr_seq_controle_p;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
