SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE finalizar_etapa_processo (nm_usuario_p text, nr_processo_p bigint) AS $body$
DECLARE


ie_ordens_nao_prep_area_w varchar(1);
nr_seq_ordem_w				bigint;
nr_seq_area_prep_w			bigint;
ie_grava_log_gedipa_w			varchar(15);

C01 CURSOR FOR
	SELECT	a.nr_sequencia,
			b.nr_seq_area_prep
	from	prescr_mat_hor b,
			adep_processo_frac a
	where	b.nr_seq_etiqueta = a.nr_sequencia
	and		a.nr_seq_processo = nr_processo_p
	and		obter_se_horario_liberado(b.dt_lib_horario, b.dt_horario) = 'S';


BEGIN

select	coalesce(max(ie_grava_log_gedipa),'S')
into STRICT	ie_grava_log_gedipa_w
from	parametros_farmacia
where	cd_estabelecimento = coalesce(wheb_usuario_pck.get_cd_estabelecimento,1);

open c01;
loop
fetch c01 into
	nr_seq_ordem_w,
	nr_seq_area_prep_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	nr_seq_ordem_w := nr_seq_ordem_w;
	CALL pemof_iniciar_finalizar_ordem(nr_seq_ordem_w,null,'F',nm_usuario_p);
	end;
end loop;
close c01;

select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ie_ordens_nao_prep_area_w
from	adep_processo_frac x,
		prescr_mat_hor y
where	y.nr_seq_etiqueta	= x.nr_sequencia
and		y.nr_seq_processo	= x.nr_seq_processo
and		x.nr_seq_processo	= nr_processo_p
and		coalesce(x.dt_fim_preparo::text, '') = ''
and		coalesce(y.dt_suspensao::text, '') = '';

if (ie_ordens_nao_prep_area_w = 'N') then
	CALL atualiza_status_processo_adep(nr_processo_p, nr_seq_area_prep_w	, 'G', 'P', clock_timestamp(), nm_usuario_p);
end if;

if (ie_grava_log_gedipa_w = 'S') then
	insert into log_gedipa(nr_sequencia,
		dt_log,
		nr_log,
		nm_objeto_execucao,
		nm_objeto_chamado,
		ds_parametros,
		ds_log,
		nr_seq_processo)
	values (obter_nextval_sequence('log_gedipa'),
		clock_timestamp(),
		2000,
		'FINALIZAR_ETAPA_PROCESSO',
		null,
		null,
		substr('NR_SEQ_PROCESSO= '||nr_processo_p|| ' - nr_seq_ordem_w= ' || nr_seq_ordem_w
					  || ' - nr_seq_area_prep_w= ' || nr_seq_area_prep_w ||
			' - NM_USUARIO= ' || wheb_usuario_pck.get_nm_usuario || ' - FUNCAO= ' || obter_funcao_ativa,1,200),
		nr_processo_p);
end if;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE finalizar_etapa_processo (nm_usuario_p text, nr_processo_p bigint) FROM PUBLIC;

